def makeyourmove (thisway, x, y) :
	xx = x
	yy = y
	if thisway == 'U': yy=yy-1 #move up
	if thisway == 'R': xx=xx+1 #move right
	if thisway == 'D': yy=yy+1 #move down
	if thisway == 'L': xx=xx-1 #move left
	if abs(xx-2)+abs(yy-2)<3 : return xx, yy
	return x, y	

with open('D2P1input.txt', 'rb') as f:
    reader = csv.reader(f)
