def number2button (num, *args) :
	for x in range(3):
		for y in range(3):
			if button2number[y][x] == num:
				args[0] = x
				args[1] = y
				return True
			else:
				return False

def makeyourmove (thisway, x, y) :
	xx = x
	yy = y
	if thisway == 'U':
		if yy > 0 : yy=yy-1 #move up if y>0
	if thisway == 'R':
		if xx < 2 : xx=xx+1 #move right if x<2
	if thisway == 'D':
		if yy < 2 : yy=yy+1 #move down if y<2
	if thisway == 'L':
		if xx > 0 : xx=xx-1 #move left if x>0
	return xx, yy	

